function getEscortImages(){$($(".escortsBlog")).each(function(n,t){const i=$(t).find(".escortImg");$(i).data("src")!=""&&common.getFile(`user/thumbnail_profile/${$(i).data("src")}`,$(i))})}function getFavoriteEscort(n,t){let i=$("#searchText").val();$(".Loading").show();$.ajax({url:"/Home/FavoriteEscorts",type:"GET",data:{search:i,pageIndex:n},success:function(i){t?$.when($("#favoriteEscorts").append(i)).done(function(){showHideLoadMoreBtn(i,n);getEscortImages()}):$.when($("#favoriteEscorts").html(i)).done(function(){showHideLoadMoreBtn(i,n);getEscortImages()});$(".Loading").hide()},error:function(){$(".Loading").hide()}})}function showHideLoadMoreBtn(n,t){if(n.trim()!="")t==1?$("#favoriteEscorts .escortsBlog").length==12?$("#loadMoreBtn").show():$("#loadMoreBtn").hide():$("#loadMoreBtn").show();else{if(t==1){const n=`<div class="col-lg-12">
                                        <div class="availableUnder">
                            No record found
                            </div>
                        </div>`;$("#favoriteEscorts").html(n)}$("#loadMoreBtn").hide()}}let pageNo=1;$(function(){getFavoriteEscort(pageNo);$("#loadMoreBtn").on("click",function(){pageNo+=1;getFavoriteEscort(pageNo,!0)})});document.addEventListener("DOMContentLoaded",function(){let t=document.getElementById("searchText"),n=document.querySelector(".clear_icons");t.addEventListener("input",function(){n.style.display=t.value.length>0?"inline":"none"});n.addEventListener("click",function(){t.value="";n.style.display="none";$("#searchText").val("");getFavoriteEscort(1,!1)})});