function getPopularEscort(n,t){let i=$("#searchText").val();$(".Loading").show();$.ajax({url:"/Home/GetPopularEscorts",type:"POST",data:{pageIndex:n,searchText:i},success:function(i){t?$.when($("#popularEscorts").append(i)).done(function(){showHideLoadMoreBtn(i,n);getEscortImages()}):$.when($("#popularEscorts").html(i)).done(function(){showHideLoadMoreBtn(i,n);getEscortImages()});$(".Loading").hide()},error:function(){$(".Loading").hide()}})}function getEscortImages(){$($(".popularEscorts .escortsBlog")).each(function(n,t){const i=$(t).find(".escortImg");$(i).data("src")!=""&&common.getFile(`user/thumbnail_profile/${$(i).data("src")}`,$(i))})}function showHideLoadMoreBtn(n,t){if(n.trim()!="")t==1?$("#popularEscorts .escortsBlog").length==12?$("#loadMoreBtn").show():$("#loadMoreBtn").hide():$("#loadMoreBtn").show();else{if(t==1){const n=`<div class="availableUnder">
                                            No record found
                                        </div>`;$("#popularEscorts").html(n)}$("#loadMoreBtn").hide()}}let pageNo=1;$(function(){getPopularEscort(pageNo,!1);$("#loadMoreBtn").on("click",function(){pageNo+=1;getPopularEscort(pageNo,!0)})});